INSERT INTO `quest_request_items` VALUES (10843, 1, 0, 0, 0, 'The Mok\'Nathal do not accept failure, or make excuses. Either you vanquish your foe, or he kills you!$B$BSince you are standing before me, you must have killed Gnosh Brognat already.', 0);
INSERT INTO `quest_request_items` VALUES (10845, 1, 0, 0, 0, 'Though you will not further yourself in my eyes, there is no shame in admitting that you aren\'t up to the challenge of slaying the mother of the Singing Ridge nether drakes.$B$BIs that what you are here to tell me?', 0);
INSERT INTO `quest_request_items` VALUES (10846, 1, 0, 0, 0, 'We have many enemies. Some we have chosen, and some, such as the Vehk\'nir, we have not.$B$BRegardless, we shall slay all of their chieftains until none stand before us.', 0);
