INSERT INTO `quest_request_items` VALUES (10853, 1, 0, 0, 0, 'The drakes of the Singing Ridge may pose a threat to us, but I think they are majestic creatures.$B$BOr, at least they were.  I hope that you will be able to save their spirits.$B$BAnd if you are in need of more totems, I have a few to spare.', 0);
