INSERT INTO `quest_request_items` VALUES (13882, 0, 0, 0, 0, 'Life is powerful, $n. Concentrated in its purest form, it can overcome anything. Someday, even Darkshore will heal from its wounds. Did you find everything I need?', 0);
INSERT INTO `quest_request_items` VALUES (13891, 0, 0, 0, 0, 'I\'m afraid to ask, $n. Is the artifact destroyed? What about its guardian?', 0);
INSERT INTO `quest_request_items` VALUES (13909, 0, 0, 0, 0, 'Wet enough for ya?', 0);
INSERT INTO `quest_request_items` VALUES (13910, 0, 0, 0, 0, 'How goes our new seaside condo? ', 0);
INSERT INTO `quest_request_items` VALUES (13912, 0, 0, 0, 0, 'Did you find it? Groff wants to clear out of these ruins as soon as possible!', 0);
INSERT INTO `quest_request_items` VALUES (13918, 0, 0, 0, 0, 'Did you assemble all the pieces of the slotted device? We may need to remove Prospector Remtravel\'s sandwich from the ancient data interface.', 0);
INSERT INTO `quest_request_items` VALUES (13922, 0, 0, 0, 0, 'Five moonstones are required to assure that the girl is healed.', 0);
INSERT INTO `quest_request_items` VALUES (13924, 0, 0, 0, 0, '$N, is that you? Have you returned with the cure for Relara?', 0);
INSERT INTO `quest_request_items` VALUES (13925, 0, 0, 0, 0, 'The sapling you carry grows in power even as we speak. Life is regenerative, $n. Did it work?', 0);
INSERT INTO `quest_request_items` VALUES (13928, 0, 0, 0, 0, 'I\'m amazed that you broke through the Horde lines and managed to get in here, $r.', 0);
INSERT INTO `quest_request_items` VALUES (13945, 0, 0, 0, 0, 'Are their numbers weakened?', 0);
INSERT INTO `quest_request_items` VALUES (13946, 0, 0, 0, 0, 'Did you have any success in routing those grellkin for us?', 0);
INSERT INTO `quest_request_items` VALUES (13949, 0, 0, 0, 0, 'Mok\'ra, stranger. Are you here from Far Watch?', 0);
INSERT INTO `quest_request_items` VALUES (13961, 0, 0, 0, 0, 'Don\'t be too harsh with our captive, he will still need his head attached to answer our questions.', 0);
INSERT INTO `quest_request_items` VALUES (13970, 0, 0, 0, 0, 'The creature doesn\'t lift its head, but looks at you imploringly with large, watery eyes.', 0);
INSERT INTO `quest_request_items` VALUES (13975, 0, 0, 0, 0, 'Was that you I saw waving a gun around from the back of my last caravan?', 0);
INSERT INTO `quest_request_items` VALUES (13979, 0, 0, 0, 0, 'Did you shut that disgusting, wart-ridden goblin up for good?', 0);
INSERT INTO `quest_request_items` VALUES (13982, 0, 0, 0, 0, 'If we are to be free, we must have Kaliva\'s heart.', 0);
INSERT INTO `quest_request_items` VALUES (14005, 0, 0, 0, 0, 'Did you find the source of the corruption?', 0);
INSERT INTO `quest_request_items` VALUES (14014, 0, 0, 0, 0, 'Bomb-throwing monkeys?! The nerve!!!', 0);
INSERT INTO `quest_request_items` VALUES (14039, 0, 0, 0, 0, 'How fare you, young one?', 0);
INSERT INTO `quest_request_items` VALUES (14154, 0, 0, 0, 0, 'Getting there!  Careful or we\'ll lose him!', 25549);
INSERT INTO `quest_request_items` VALUES (14109, 0, 0, 0, 0, 'Do you have it all, $N? I can\'t wait to see it on you!$B$BYou bought the good stuff, right?', 0);
INSERT INTO `quest_request_items` VALUES (14110, 6, 0, 0, 0, 'Do you have it all? I can\'t wait to see it on you!$B$BMmm hmm!', 0);
INSERT INTO `quest_request_items` VALUES (14118, 0, 0, 0, 0, 'You smell of fresh kill, $c.', 0);
INSERT INTO `quest_request_items` VALUES (14121, 6, 0, 0, 0, 'Were you able to steal a whole bunch of stolen loot?', 0);
INSERT INTO `quest_request_items` VALUES (14122, 0, 0, 0, 0, 'Did you get it? Did you crack the safe and get your personal riches out of the bank?', 0);
INSERT INTO `quest_request_items` VALUES (14123, 6, 0, 0, 0, 'You got the goods, boss?', 0);
INSERT INTO `quest_request_items` VALUES (14124, 6, 0, 0, 0, 'How much kaja\'mite do you have?', 0);
INSERT INTO `quest_request_items` VALUES (14126, 1, 0, 0, 0, 'Oh, $N, it\'s you. What an unexpected surprise.$B$B<The Trade Prince rubs his hands together.>', 0);
INSERT INTO `quest_request_items` VALUES (14127, 0, 0, 0, 0, 'You found something on one of the bodies, you say? Give it here!', 0);
INSERT INTO `quest_request_items` VALUES (14128, 0, 0, 0, 0, 'I trust this interruption is in regards to something important?', 0);
INSERT INTO `quest_request_items` VALUES (14129, 0, 0, 0, 0, '<Labor Captain Grabbit\'s voice crackles over the radio.>$B$BHey $N! Are you reading this?$B$BPiloting that sucker is easy. Push the pedals for your pedal controls and, ah, move the levers for your lever controls. Got it?', 0);
INSERT INTO `quest_request_items` VALUES (14134, 0, 0, 0, 0, 'How\'s the wood processing going? You should see what that thing does to broccoli!', 0);
INSERT INTO `quest_request_items` VALUES (14138, 3, 0, 0, 0, 'Um... everything\'s going great. Yeah.$B$BWhat\'s that?', 0);
INSERT INTO `quest_request_items` VALUES (14094, 0, 0, 0, 0, 'Have you salvaged the supplies, $n?', 25383);
INSERT INTO `quest_request_items` VALUES (14197, 0, 0, 0, 0, 'Did you get my shipment together?', 0);
INSERT INTO `quest_request_items` VALUES (14204, 0, 0, 0, 0, 'Is it done yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (14225, 0, 0, 0, 0, 'The Burning Blade must not be allowed to succeed with their plan!', 0);
INSERT INTO `quest_request_items` VALUES (14232, 0, 0, 0, 0, 'What have you there, $c?', 0);
INSERT INTO `quest_request_items` VALUES (14233, 0, 0, 0, 0, '$N, find anything interesting in the cavern?', 0);
INSERT INTO `quest_request_items` VALUES (14234, 0, 0, 0, 0, '<The orc narrows her eyes at you.>$B$BYou must have been on the goblin slave ship that was sunk. What\'s that you have in your hand?', 0);
INSERT INTO `quest_request_items` VALUES (14241, 0, 0, 0, 0, 'Hmm, looks like overly-complicated gnomish engineering with lots of unnecessary instrumentation and safety precautions.', 0);
INSERT INTO `quest_request_items` VALUES (14251, 0, 0, 0, 0, 'Let\'s have a look at those scorpion tails, friend.', 0);
INSERT INTO `quest_request_items` VALUES (14252, 0, 0, 0, 0, 'How\'s the search for tail coming, $N?', 0);
INSERT INTO `quest_request_items` VALUES (14253, 0, 0, 0, 0, 'How is the search for swoops coming?', 0);
INSERT INTO `quest_request_items` VALUES (14254, 0, 0, 0, 0, 'Whatcha got there, $c?', 0);
INSERT INTO `quest_request_items` VALUES (14257, 0, 0, 0, 0, 'Have you collected the components I require?', 0);
INSERT INTO `quest_request_items` VALUES (14258, 0, 0, 0, 0, 'I hope you have more ammo for me. I\'ve got nothing to shoot off over here but my mouth!', 0);
INSERT INTO `quest_request_items` VALUES (14268, 0, 0, 0, 0, 'Could it be...?', 0);
INSERT INTO `quest_request_items` VALUES (14282, 0, 0, 0, 0, 'What have you learned, $c?', 0);
INSERT INTO `quest_request_items` VALUES (14304, 0, 0, 0, 0, 'Were you able to recover the samples I require?', 0);
INSERT INTO `quest_request_items` VALUES (14305, 0, 0, 0, 0, 'Have you collected the samples I require?', 0);
INSERT INTO `quest_request_items` VALUES (14308, 0, 0, 0, 0, '<Greely\'s squeaky voice peels through a set of speakers.>$B$BRocketway to Secret Lab! Rocketway to Secret Lab! Come in, Secret Lab...', 0);
INSERT INTO `quest_request_items` VALUES (14310, 0, 0, 0, 0, 'Don\'t worry about the residual effects of all those burning chemicals in the lab. Hair grows back!', 0);
INSERT INTO `quest_request_items` VALUES (14316, 0, 0, 0, 0, 'Were you able to find our belongings, $N?', 0);
INSERT INTO `quest_request_items` VALUES (14325, 0, 0, 0, 0, 'Have you brought food for my clan, $r?', 0);
INSERT INTO `quest_request_items` VALUES (14339, 0, 0, 0, 0, 'Back so soon, $c?', 0);
INSERT INTO `quest_request_items` VALUES (14362, 0, 0, 0, 0, 'What have you there, $c?', 0);
INSERT INTO `quest_request_items` VALUES (14363, 0, 0, 0, 0, 'The Burning Blade must not be allowed to succeed with their plan!', 0);
INSERT INTO `quest_request_items` VALUES (14370, 0, 0, 0, 0, 'Have you got more samples? Careless adventurers have been looting this stuff for years - they don\'t know how precious it is!', 0);
INSERT INTO `quest_request_items` VALUES (14371, 0, 0, 0, 0, 'I probably should\'ve warned you that mistwing chimaeras can shoot lightning. ', 0);
INSERT INTO `quest_request_items` VALUES (14378, 0, 0, 0, 0, 'Well, $c? Did yeh find the cretin?', 0);
INSERT INTO `quest_request_items` VALUES (14379, 0, 0, 0, 0, 'Were you able to collect some lobstrock tails, $r?', 0);
INSERT INTO `quest_request_items` VALUES (14385, 0, 0, 0, 0, 'Once you have the laxative, try to feed it to Gormungan. And maybe give him a magazine or something. Do it for science!', 0);
INSERT INTO `quest_request_items` VALUES (14395, 0, 0, 0, 0, 'There aren\'t many of us left, $N.  We have to look out for those remaining Gilneans.', 0);
INSERT INTO `quest_request_items` VALUES (14400, 0, 0, 0, 0, 'Did you find everything okay, dear?', 25549);
INSERT INTO `quest_request_items` VALUES (24472, 0, 0, 0, 0, 'How did it go, $n?', 25383);
INSERT INTO `quest_request_items` VALUES (24495, 0, 0, 0, 0, 'Any luck finding those pages?', 25549);
INSERT INTO `quest_request_items` VALUES (24628, 0, 0, 0, 0, 'You\'ve returned, $n.', 25383);
INSERT INTO `quest_request_items` VALUES (14408, 0, 0, 0, 0, 'I suspect the archives are close, $N. Locate those plans!', 0);
INSERT INTO `quest_request_items` VALUES (14456, 0, 0, 0, 0, 'How many have you brought me, initiate?', 0);
INSERT INTO `quest_request_items` VALUES (14460, 0, 0, 0, 0, 'Have you completed your trials, young one?', 0);
INSERT INTO `quest_request_items` VALUES (20440, 0, 0, 0, 0, 'Do you have the paws and talons, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24432, 0, 0, 0, 0, 'It is good to see that you\'re still alive, $r.$b$bHave you found the items I require?', 0);
INSERT INTO `quest_request_items` VALUES (24441, 0, 0, 0, 0, '$N, do you have the claws I sent you to gather?  The Thunderhorn Water Well grows more tainted with each passing hour!', 0);
INSERT INTO `quest_request_items` VALUES (24474, 22, 0, 0, 0, 'The ale, darling, the ale!', 24742);
INSERT INTO `quest_request_items` VALUES (24475, 0, 0, 0, 0, 'The people are gettin\' restless.  D\'ye have the supplies yet?', 24742);
INSERT INTO `quest_request_items` VALUES (24477, 0, 0, 0, 0, 'Yes?', 24742);
INSERT INTO `quest_request_items` VALUES (24486, 0, 0, 0, 0, 'Did you manage to pry any artifacts from the troggs?', 24742);
INSERT INTO `quest_request_items` VALUES (24488, 397, 0, 0, 0, 'We\'re counting on you, $N!', 0);
INSERT INTO `quest_request_items` VALUES (24492, 0, 0, 0, 0, 'Are you absolutely certain you\'ve taken care of everything?  You won\'t have time to turn back.', 24742);
INSERT INTO `quest_request_items` VALUES (24504, 0, 0, 0, 0, '<Mankrik snarls.>\n\nWhat is it!? I\'m busy!', 0);
INSERT INTO `quest_request_items` VALUES (24513, 0, 0, 0, 0, 'Peake is a monster. If his superiors knew, they would thank the Horde for ending him.', 0);
INSERT INTO `quest_request_items` VALUES (24514, 0, 0, 0, 0, 'Terrortooth raptors hunt in packs and surround their prey. We hunt them, yes; but we also respect them.', 0);
INSERT INTO `quest_request_items` VALUES (24518, 0, 0, 0, 0, 'Ah, you say you found something on one of the bodies?', 0);
INSERT INTO `quest_request_items` VALUES (24523, 0, 0, 0, 0, '$N.  Do you have the teeth I asked for?', 0);
INSERT INTO `quest_request_items` VALUES (24534, 0, 0, 0, 0, 'Have you taken the Bristlebacks down a peg or two?', 0);
INSERT INTO `quest_request_items` VALUES (24542, 0, 0, 0, 0, 'I am extremely busy, $c.', 0);
INSERT INTO `quest_request_items` VALUES (24551, 0, 0, 0, 0, 'You look like the type of $c who gets things done. What news do you have from the field?', 0);
INSERT INTO `quest_request_items` VALUES (24569, 0, 0, 0, 0, 'Why did Gar\'dul allow the Alliance to create such a buildup of heavy siege vehicles? Why wasn\'t a strike ordered until I got here? Gar\'dul will have to answer for this...', 0);
INSERT INTO `quest_request_items` VALUES (24570, 0, 0, 0, 0, 'Have you collected enough? We\'ll need several for proper comparison.', 0);
INSERT INTO `quest_request_items` VALUES (24571, 0, 0, 0, 0, 'Do you have the seeds?', 0);
INSERT INTO `quest_request_items` VALUES (24573, 0, 0, 0, 0, 'Have their spirits found peace with the Earthmother?', 0);
INSERT INTO `quest_request_items` VALUES (24574, 0, 0, 0, 0, 'Cutting down the outgrowth will merely buy us some time. Have you found any suspicious pieces?', 0);
INSERT INTO `quest_request_items` VALUES (24592, 0, 0, 0, 0, 'Careful, $N.', 0);
INSERT INTO `quest_request_items` VALUES (24604, 0, 0, 0, 0, '<Mankrik snarls.>$B$BWhat is it!? I\'m busy!', 0);
INSERT INTO `quest_request_items` VALUES (24606, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_request_items` VALUES (24616, 0, 0, 0, 0, 'Is it done, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24619, 0, 0, 0, 0, 'Did you get my parts? Let me see them!', 0);
INSERT INTO `quest_request_items` VALUES (24620, 0, 0, 0, 0, 'You\'ve gotta have heart, $N. All you really need is heart.', 0);
INSERT INTO `quest_request_items` VALUES (24621, 0, 0, 0, 0, 'Brraaaaaaiiinnnnss...', 0);
INSERT INTO `quest_request_items` VALUES (26706, 0, 0, 0, 0, 'The only risks worth taking are the ones that lead to victories worth having.', 25549);
INSERT INTO `quest_request_items` VALUES (24631, 0, 0, 0, 0, 'The Wildhammer are fierce fighters, in the air or on the ground. How goes the battle?', 0);
INSERT INTO `quest_request_items` VALUES (24634, 0, 0, 0, 0, 'Watch your back, $c. They could be anywhere.', 0);
INSERT INTO `quest_request_items` VALUES (24641, 0, 0, 0, 0, 'I doubt dey too much of a challenge, mon, but they\'ll still claw up ya ankles if ya don\'t stay quick footed.', 0);
INSERT INTO `quest_request_items` VALUES (24653, 0, 0, 0, 0, 'Do you have the skulls?', 0);
INSERT INTO `quest_request_items` VALUES (24654, 0, 0, 0, 0, 'The Alliance is nothing. Swarms of those critter-crawlys?\n\n<Crawgol shudders.>', 0);
INSERT INTO `quest_request_items` VALUES (24667, 0, 0, 0, 0, 'Ah, do you bring news from up north?', 0);
INSERT INTO `quest_request_items` VALUES (24685, 0, 0, 0, 0, 'Give me the plans, quickly! Our time grows short.\n\nMostly because I\'ve been eating my disguise.', 0);
INSERT INTO `quest_request_items` VALUES (24741, 6, 0, 0, 0, 'How many did you get? I can just taste the massive omelet we\'re going to cook up now!', 27602);
INSERT INTO `quest_request_items` VALUES (24744, 1, 0, 0, 0, 'Just think about the accolades that will rain down upon me... us, when the Biggest Egg Ever is returned!', 27602);
INSERT INTO `quest_request_items` VALUES (24753, 0, 0, 0, 0, 'I doubt dey too much of a challenge, mon, but they\'ll still claw up ya ankles if ya don\'t stay quick footed.', 0);
INSERT INTO `quest_request_items` VALUES (24779, 0, 0, 0, 0, 'I doubt dey too much of a challenge, mon, but they\'ll still claw up ya ankles if ya don\'t stay quick footed.', 0);
INSERT INTO `quest_request_items` VALUES (24816, 1, 0, 0, 0, '$n, take it easy on Grapplehammer. He\'s a genius, but he\'s got a fragile ego.', 27602);
INSERT INTO `quest_request_items` VALUES (24824, 0, 0, 0, 0, 'The elements must be carefully balanced, so returning the essence is key.  I hope you\'ve been collecting it...', 0);
INSERT INTO `quest_request_items` VALUES (24859, 6, 0, 0, 0, 'Well? How many do you have for me? I think that a 95 to 5 split is more than generous.', 27602);
INSERT INTO `quest_request_items` VALUES (24863, 0, 0, 0, 0, 'What\'s the story out there?', 0);
INSERT INTO `quest_request_items` VALUES (24920, 0, 0, 0, 0, 'Is it done, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24930, 0, 0, 0, 0, 'What is it then, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24931, 0, 0, 0, 0, 'Do, ah, do you have them?', 0);
INSERT INTO `quest_request_items` VALUES (24933, 0, 0, 0, 0, 'This is fresh, right? They won\'t go after anything that\'s been withering in the sun.', 0);
INSERT INTO `quest_request_items` VALUES (24941, 0, 0, 0, 0, 'Paxton has already loaded a charge into the cannon. All it needs is the ammunition you improvised.', 0);
INSERT INTO `quest_request_items` VALUES (24944, 0, 0, 0, 0, 'I hope you have enough bandages. My supply of those herbs was cut off when these brutes sacked my beloved Gilneas.', 0);
INSERT INTO `quest_request_items` VALUES (24946, 1, 0, 0, 0, 'Hmm, let me see how much you\'ve got.', 27602);
INSERT INTO `quest_request_items` VALUES (24948, 0, 0, 0, 0, 'Burn it all, $c. Burn it to cinders!', 0);
INSERT INTO `quest_request_items` VALUES (24952, 1, 0, 0, 0, 'Well there you are!', 27602);
INSERT INTO `quest_request_items` VALUES (24953, 0, 0, 0, 0, 'I didn\'t hear no cave-ins, pal! Try again!', 0);
INSERT INTO `quest_request_items` VALUES (24954, 1, 0, 0, 0, 'No time like the present to be done with the simple task I put before you, $n. Wouldn\'t you agree?', 27602);
INSERT INTO `quest_request_items` VALUES (24956, 0, 0, 0, 0, 'Is he dead? Do you have his ... helm?', 0);
INSERT INTO `quest_request_items` VALUES (24960, 0, 0, 0, 0, 'No dilly-dallying now.  You\'re among the Forsaken now, and we hold ours to a higher standard.', 0);
INSERT INTO `quest_request_items` VALUES (24961, 0, 0, 0, 0, 'Have you spoken with her yet?  She needs to be shown the truth.', 0);
INSERT INTO `quest_request_items` VALUES (24972, 0, 0, 0, 0, 'Yes, what do you want?', 0);
INSERT INTO `quest_request_items` VALUES (24974, 0, 0, 0, 0, 'Oh, the pain of loneliness is too much to bear!', 0);
INSERT INTO `quest_request_items` VALUES (24975, 0, 0, 0, 0, 'Were you able to gather 10 pumpkins yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24976, 0, 0, 0, 0, 'I\'ve got a feeling this will be my best batch yet, good $c.', 0);
INSERT INTO `quest_request_items` VALUES (24977, 0, 0, 0, 0, 'Stay away, foul and unholy creature!  May the Light protect me!  The Scarlet Crusade shall rid Azeroth of your...$b$b...oh wait.  Is that food for me?  I am so hungry...', 0);
INSERT INTO `quest_request_items` VALUES (24979, 0, 0, 0, 0, 'A letter?  What does it say?', 0);
INSERT INTO `quest_request_items` VALUES (24980, 0, 0, 0, 0, 'If you wish to prove your worth to The Dark Lady, slay 10 Scarlet Warriors, $c.', 0);
INSERT INTO `quest_request_items` VALUES (24981, 0, 0, 0, 0, 'We shall herd the Scarlet Crusade like cattle to their demise.', 0);
INSERT INTO `quest_request_items` VALUES (24988, 0, 0, 0, 0, 'Do you have five duskbat pelts yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (24990, 0, 0, 0, 0, 'Have you collected the darkhound blood yet, $N? Time is fleeting!', 0);
INSERT INTO `quest_request_items` VALUES (24994, 0, 0, 0, 0, '<The apothecary mutters to himself.>$b$bThey brought me gloom weed?  GLOOM WEED?!', 0);
INSERT INTO `quest_request_items` VALUES (24995, 0, 0, 0, 0, '$N, were you able to obtain those scales from the murlocs?', 0);
INSERT INTO `quest_request_items` VALUES (24996, 0, 0, 0, 0, 'Why if I had my trusted rifle you\'d be as good as dead, $C.  Just wait until the Steam Tank Brigade arrives to rescue me!', 0);
INSERT INTO `quest_request_items` VALUES (24997, 0, 0, 0, 0, 'Is your task complete?  Have you destroyed those dog-things and drained them of their ichor?', 0);
INSERT INTO `quest_request_items` VALUES (24998, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_request_items` VALUES (25000, 0, 0, 0, 0, 'What is it, $c? This here camp ain\'t fit for sight-seein\'.', 0);
INSERT INTO `quest_request_items` VALUES (25002, 0, 0, 0, 0, 'What? My supply caravan was sacked by those cold-blooded scissor-paw things?', 0);
INSERT INTO `quest_request_items` VALUES (25004, 0, 0, 0, 0, 'Our battles with the Scourge wage on, $N.  Do your part and throw those cursed, mindless undead back into the Plaguelands!', 0);
INSERT INTO `quest_request_items` VALUES (25005, 0, 0, 0, 0, 'The fetor of Scourge hangs about you, $n.  What is it you seek?', 0);
INSERT INTO `quest_request_items` VALUES (25009, 0, 0, 0, 0, 'I see you have returned but your task is not complete.  Perhaps you are at ease with the mere humans interfering with The Dark Lady\'s plan?  Or perhaps you can heed the call of duty and slay the Scarlet band that lies only moments away!', 0);
INSERT INTO `quest_request_items` VALUES (25013, 0, 0, 0, 0, 'Do you have some venom from a Vicious Night Web Spider yet, $N?  It\'s the final component I need in order to test my experiment.', 0);
INSERT INTO `quest_request_items` VALUES (25015, 0, 0, 0, 0, 'I should mention, you\'re probably going to need a machete to hack through this mess.\n\nThis jungle came out of nowhere!', 0);
INSERT INTO `quest_request_items` VALUES (25021, 0, 0, 0, 0, 'Ya ain\'t been drinkin\' it yaself, eh? I need all of it...', 0);
INSERT INTO `quest_request_items` VALUES (25024, 0, 0, 0, 0, 'There are yet more Alliance paratroopers that need to be dispatched if we\'re to get about the business of escaping from this island, $N.', 0);
INSERT INTO `quest_request_items` VALUES (25025, 0, 0, 0, 0, 'Did the spirits speak true, mon? You got it?', 0);
INSERT INTO `quest_request_items` VALUES (25026, 0, 0, 0, 0, 'You got de stuff, mon?', 0);
INSERT INTO `quest_request_items` VALUES (25027, 0, 0, 0, 0, 'I can see why the Horde hates these little nuisances. Have you taken on Three-Tooth yet?', 0);
INSERT INTO `quest_request_items` VALUES (25028, 0, 0, 0, 0, 'I am very busy, $c. This uncontrolled growth threatens to engulf the continent. Do you have any news?', 0);
INSERT INTO `quest_request_items` VALUES (25029, 0, 0, 0, 0, 'Do you have the remains of the Agamands?  Are those cursed beasts finally destroyed?', 0);
INSERT INTO `quest_request_items` VALUES (24767, 0, 0, 0, 0, 'I doubt dey too much of a challenge, mon, but they\'ll still claw up ya ankles if ya don\'t stay quick footed.', 28153);
INSERT INTO `quest_request_items` VALUES (24625, 1, 0, 0, 0, 'Da naga have been our enemies for a long, long time, and our previous encounters with dem have not gone well. It be clear dat dis time will be different. Tings have changed, and you be part of dat change.', 28153);
INSERT INTO `quest_request_items` VALUES (25038, 0, 0, 0, 0, 'Where\'s that useless abomination? Either you have what I need or you\'d best get out there and find it.', 0);
INSERT INTO `quest_request_items` VALUES (25042, 0, 0, 0, 0, '<Trawley licks his lips, barely able to constrain his drool.>\n\nYou get my steaks yet? Nice and bloody?', 0);
INSERT INTO `quest_request_items` VALUES (25044, 0, 0, 0, 0, 'Sabersnout! Is he dead? What\'s the news?', 0);
INSERT INTO `quest_request_items` VALUES (25048, 0, 0, 0, 0, 'Do you have a whole buncha boxes? Let\'s see!', 0);
INSERT INTO `quest_request_items` VALUES (25054, 0, 0, 0, 0, 'I hope you don\'t think you\'re hiding any for yourself, silly! These goggles can see right through your bags.$B$BAnd your armor.', 0);
INSERT INTO `quest_request_items` VALUES (25056, 0, 0, 0, 0, 'Let\'s see... do you have enough hides?', 0);
INSERT INTO `quest_request_items` VALUES (25057, 0, 0, 0, 0, 'Is the Taurajo situation under control?', 0);
INSERT INTO `quest_request_items` VALUES (25059, 0, 0, 0, 0, 'Have you searched the ruins yet?', 0);
INSERT INTO `quest_request_items` VALUES (25066, 0, 0, 0, 0, 'What are you doing back here? Get up there and shoot them down!', 0);
INSERT INTO `quest_request_items` VALUES (25075, 0, 0, 0, 0, 'What have you got for me?', 0);
INSERT INTO `quest_request_items` VALUES (25079, 0, 0, 0, 0, 'Do you have a keg of Blast-O powder yet?\n\nWhen you get one ... don\'t shake it.', 0);
INSERT INTO `quest_request_items` VALUES (25080, 0, 0, 0, 0, 'It must be an enormous evolutionary advantage to be able to shoot lightning. From your face.', 0);
INSERT INTO `quest_request_items` VALUES (25082, 0, 0, 0, 0, 'How goes the fight, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25087, 0, 0, 0, 0, 'Hrm, news from the fortress? Well. Spit it out!', 0);
INSERT INTO `quest_request_items` VALUES (25090, 0, 0, 0, 0, 'Have you completed the task with which I have charged you?  Do you have those putrid claws?', 0);
INSERT INTO `quest_request_items` VALUES (25093, 6, 0, 0, 0, 'Has justice been served?', 27602);
INSERT INTO `quest_request_items` VALUES (25106, 0, 0, 0, 0, 'How\'s the situation down there?\n\nWhen the raid started I popped a bullet into their ringleader, Stonespire, but they just kept right on coming.', 0);
INSERT INTO `quest_request_items` VALUES (25110, 6, 0, 0, 0, 'Huh?', 27602);
INSERT INTO `quest_request_items` VALUES (25172, 0, 0, 0, 0, 'Quickly, $n.  Dispatch of these humans in our valley.', 20574);
INSERT INTO `quest_request_items` VALUES (25151, 0, 0, 0, 0, '<Marley\'s breathing is ragged, coming and going in shuddering gasps.>\n\n<His eyes look at you imploringly.>', 0);
INSERT INTO `quest_request_items` VALUES (25170, 0, 0, 0, 0, 'You bring the jujus, I show you my mojo.', 0);
INSERT INTO `quest_request_items` VALUES (25176, 0, 0, 0, 0, '$g<Gail purrs disarmingly at you.>: <Gail glares menacingly at you.>;', 0);
INSERT INTO `quest_request_items` VALUES (25178, 0, 0, 0, 0, '$g Has my knight in shining armor returned?: Well?;', 0);
INSERT INTO `quest_request_items` VALUES (25190, 0, 0, 0, 0, 'I hate quilboar!', 0);
INSERT INTO `quest_request_items` VALUES (25193, 0, 0, 0, 0, 'Kron will be remembered.', 0);
INSERT INTO `quest_request_items` VALUES (25201, 1, 0, 0, 0, 'Let\'s see what you have there, $n.', 27602);
INSERT INTO `quest_request_items` VALUES (25202, 0, 0, 0, 0, 'What did you decide? Death or forgiveness?', 0);
INSERT INTO `quest_request_items` VALUES (25218, 0, 0, 0, 0, 'If only air weren\'t so darn floaty! Or if only those water breathing spells came with stylish helmets! Either would do nicely.', 0);
INSERT INTO `quest_request_items` VALUES (25219, 0, 0, 0, 0, 'I know, this isn\'t the most desirable work, but can you really help but be curious about that shell? It\'s immense. There has to be something more to it.', 0);
INSERT INTO `quest_request_items` VALUES (25224, 0, 0, 0, 0, 'Yes, $Ndamus?', 0);
INSERT INTO `quest_request_items` VALUES (25232, 0, 0, 0, 0, 'The scrolls, $N, the scrolls!', 0);
INSERT INTO `quest_request_items` VALUES (25234, 0, 0, 0, 0, 'Have you created some havoc down there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25243, 6, 0, 0, 0, 'What did you decide to do, hon?', 27602);
INSERT INTO `quest_request_items` VALUES (25260, 0, 0, 0, 0, 'Run into any trouble on the way?', 0);
INSERT INTO `quest_request_items` VALUES (25262, 0, 0, 0, 0, 'Have you yet entered Skull Rock, $N?  Do you have the Searing Collars?', 0);
INSERT INTO `quest_request_items` VALUES (25263, 0, 0, 0, 0, 'My most humble greetings, $c.  How might I help my $r $gbrother:sister; today?', 0);
INSERT INTO `quest_request_items` VALUES (25266, 6, 0, 0, 0, 'What be your business in Orgrimmar?', 27602);
INSERT INTO `quest_request_items` VALUES (25267, 0, 0, 0, 0, 'What is this, goblin? Your kind are not allowed inside Grommash Hold.$B$BLeave before I have to teach you your place.', 0);
INSERT INTO `quest_request_items` VALUES (25270, 0, 0, 0, 0, 'These animals were once men, but they\'ve turned into mindless beasts under the influence of the Twilight\'s hammer. Do not hesitate to slay them all.', 0);
INSERT INTO `quest_request_items` VALUES (25271, 0, 0, 0, 0, 'Worgen like myself were drawn to the story of Goldrinn, his strength and wisdom. But these vandals - they worship a different aspect of the wolf god entirely.', 0);
INSERT INTO `quest_request_items` VALUES (25273, 0, 0, 0, 0, 'Your bravery is an inspiration to others, $c. Tell me your name.', 0);
INSERT INTO `quest_request_items` VALUES (25274, 0, 0, 0, 0, 'Did you get the papers?', 0);
INSERT INTO `quest_request_items` VALUES (25276, 0, 0, 0, 0, 'You will address me with the respect I am due, recruit.', 0);
INSERT INTO `quest_request_items` VALUES (25296, 0, 0, 0, 0, 'Did you get the documents, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25297, 0, 0, 0, 0, '<The cauldron burbles, filled with a disgusting liquid.>', 0);
INSERT INTO `quest_request_items` VALUES (25298, 0, 0, 0, 0, 'Did the draught work?', 0);
INSERT INTO `quest_request_items` VALUES (25303, 0, 0, 0, 0, '<You pour over the Apocrypha, trying to make sense of it all.>', 0);
INSERT INTO `quest_request_items` VALUES (25321, 0, 0, 0, 0, 'The key, $N.  Did you get it?', 0);
INSERT INTO `quest_request_items` VALUES (25325, 0, 0, 0, 0, 'Is Staghelm with you, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25328, 0, 0, 0, 0, 'Key? Now? Please?', 0);
INSERT INTO `quest_request_items` VALUES (25353, 0, 0, 0, 0, 'We meet again, $c. You carve a path of fear and confusion in your wake.$B$B<Goldrinn\'s eyes twinkle expectantly.> ', 0);
INSERT INTO `quest_request_items` VALUES (25355, 0, 0, 0, 0, 'We meet again, $c. You fight well! You carve a path of fear and confusion in your wake.$B$B<Lo\'Gosh peers at you, expectantly.>', 0);
INSERT INTO `quest_request_items` VALUES (25358, 0, 0, 0, 0, 'Were ya able to grab some supplies, $r?', 0);
INSERT INTO `quest_request_items` VALUES (25366, 0, 0, 0, 0, 'Welcome back $n - were you able to find some tangible evidence as to the plans of the gnolls?', 0);
INSERT INTO `quest_request_items` VALUES (25367, 0, 0, 0, 0, 'How goes the hunt, $N?  Are you here to report your success?', 0);
INSERT INTO `quest_request_items` VALUES (25369, 0, 0, 0, 0, 'Have you managed to bring down Stinglasher yet?', 0);
INSERT INTO `quest_request_items` VALUES (25372, 0, 0, 0, 0, 'I am at my wit\'s end, $N! Aessina is silent.$B$BHave you found something? What is this?', 0);
INSERT INTO `quest_request_items` VALUES (25377, 0, 0, 0, 0, 'This slightly encrusted lock might still be functional.', 0);
INSERT INTO `quest_request_items` VALUES (25385, 0, 0, 0, 0, 'Did you save the wee animals? And their wee little friends? ', 0);
INSERT INTO `quest_request_items` VALUES (25388, 0, 0, 0, 0, 'What have you got there, $n?', 0);
INSERT INTO `quest_request_items` VALUES (25389, 0, 0, 0, 0, 'Oh, please tell me you were able to find me some food, $c.', 0);
INSERT INTO `quest_request_items` VALUES (25390, 0, 0, 0, 0, 'Well, $r?$b$bWere you able to recover my valuables?', 0);
INSERT INTO `quest_request_items` VALUES (25396, 0, 0, 0, 0, 'The tears are among the very rocks that the giants themselves rise from. There should be loose ones near the base of these large monoliths.', 0);
INSERT INTO `quest_request_items` VALUES (25401, 0, 0, 0, 0, 'Do you have the orb?', 0);
INSERT INTO `quest_request_items` VALUES (25402, 0, 0, 0, 0, 'What is that you\'re holding?', 0);
INSERT INTO `quest_request_items` VALUES (25408, 0, 0, 0, 0, 'Has the local flora been giving you a hard time?$B$B<Blackhorn grins disturbingly.>', 0);
INSERT INTO `quest_request_items` VALUES (25419, 0, 0, 0, 0, 'You examine the chain, hoping it can be restored.', 0);
INSERT INTO `quest_request_items` VALUES (25422, 0, 0, 0, 0, 'Do you have the soil? I\'m having trouble staying together...hurry...', 0);
INSERT INTO `quest_request_items` VALUES (25423, 0, 0, 0, 0, 'Have you gathered it?', 0);
INSERT INTO `quest_request_items` VALUES (25426, 0, 0, 0, 0, 'The manes aren\'t just for trophies. If there\'s to be slaughter, we may as well not let it be a total waste.', 0);
INSERT INTO `quest_request_items` VALUES (25428, 0, 0, 0, 0, 'Tell me! Have you fought Thol\'embaar yet?$B$BI am living vicariously through you, $r. Do not disappoint!', 0);
INSERT INTO `quest_request_items` VALUES (25429, 0, 0, 0, 0, 'How goes the hunt, $N?  Are you here to report your success?', 0);
INSERT INTO `quest_request_items` VALUES (25442, 0, 0, 0, 0, 'Yes, $c? Have you found something?', 0);
INSERT INTO `quest_request_items` VALUES (25447, 0, 0, 0, 0, 'This is a sad task, $n, but necessary.', 0);
INSERT INTO `quest_request_items` VALUES (25449, 0, 0, 0, 0, 'A stack of ten thick yeti hides will net you a choice of one of my crafted items.  Because I care about quality, I am able to ensure you\'ll get a good item!$B$B', 0);
INSERT INTO `quest_request_items` VALUES (25450, 0, 0, 0, 0, 'These hides may not be as resilient as thorium, but they\'re nice and supple.', 0);
INSERT INTO `quest_request_items` VALUES (25458, 0, 0, 0, 0, 'Has the general fallen?', 0);
INSERT INTO `quest_request_items` VALUES (25465, 0, 0, 0, 0, 'I had to tweak the thing to stop working on mountain giants. Even though I wasn\'t the one running around zapping things, those night elves beat the copper out of me after they found those guys shrunk.', 0);
INSERT INTO `quest_request_items` VALUES (25466, 0, 0, 0, 0, 'While I could bore you with the technical details of the utility behind elemental cores, I will instead tempt you to completion of this important task by offering valuable coin and prizes for doing so.  Yon verily, are you finished?', 0);
INSERT INTO `quest_request_items` VALUES (25472, 0, 0, 0, 0, 'Yes, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25473, 5, 0, 0, 0, '<Sassy eyes the six-pack warily, as if she expects it to explode as soon as you hand it to her.>$B$BWe need more Kaja\'Cola than this!$B$BI swear I\'m going to fire Dampwick if I don\'t kill him first!', 0);
INSERT INTO `quest_request_items` VALUES (25475, 0, 0, 0, 0, 'You appear to have uncovered the wreckage of... a robotic chicken?  The voice from within the egg crackles to life again:$B$B\"Yes, excellent work!  This is indeed my homing robot, though my sensors indicate that it needs a jump start before it can fly back to Booty Bay for repairs.  Go ahead and place the beacon inside the rover - the beacon will take care of the rest!\"', 0);
INSERT INTO `quest_request_items` VALUES (25477, 0, 0, 0, 0, 'Were you able to tame a sea turtle, $n?', 0);
INSERT INTO `quest_request_items` VALUES (25491, 0, 0, 0, 0, 'I suspect life still struggles beneath the ash. What were you able to find?', 0);
INSERT INTO `quest_request_items` VALUES (25493, 0, 0, 0, 0, 'Have you collected the innards?$B$BCore hounds are not native to this part of the world, they accompanied Twilight\'s Hammer from the nether regions of the earth. Do not trouble yourself over their elimination.', 0);
INSERT INTO `quest_request_items` VALUES (25494, 0, 0, 0, 0, 'Did you get it?', 0);
INSERT INTO `quest_request_items` VALUES (25505, 0, 0, 0, 0, 'Is that my list of parts from Pozzik?', 0);
INSERT INTO `quest_request_items` VALUES (25507, 0, 0, 0, 0, 'Nemesis is a molten ball of fury protected by a stony shell of pure hatred.$B$BHis time will come.', 0);
INSERT INTO `quest_request_items` VALUES (25516, 0, 0, 0, 0, 'We can get started as soon as you get everything that\'s on the list.', 0);
INSERT INTO `quest_request_items` VALUES (25521, 0, 0, 0, 0, 'Do you have them? Oh, please...', 0);
INSERT INTO `quest_request_items` VALUES (25522, 0, 0, 0, 0, 'You\'re back! Do you have it?', 0);
INSERT INTO `quest_request_items` VALUES (25552, 0, 0, 0, 0, 'You\'ve returned, $r.', 0);
INSERT INTO `quest_request_items` VALUES (25554, 0, 0, 0, 0, 'Did you succeed in your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25594, 0, 0, 0, 0, 'Never underestimate the appeal of something shiny.', 0);
INSERT INTO `quest_request_items` VALUES (25595, 0, 0, 0, 0, 'With all the rumbling bellies, it sounds like there\'s a monster in this cave. It\'s putting my nerves on edge.', 0);
INSERT INTO `quest_request_items` VALUES (25599, 0, 0, 0, 0, 'The tome! Have you found it?', 0);
INSERT INTO `quest_request_items` VALUES (25602, 0, 0, 0, 0, 'Did you get the explosives from the camp, $c?', 0);
INSERT INTO `quest_request_items` VALUES (25607, 0, 0, 0, 0, 'Ze junk? Do you have zit?', 0);
INSERT INTO `quest_request_items` VALUES (25610, 0, 0, 0, 0, 'You already have all of that sunken treasure?', 0);
INSERT INTO `quest_request_items` VALUES (25628, 0, 0, 0, 0, 'What\'s going on? You gonna get me out of here?', 0);
INSERT INTO `quest_request_items` VALUES (25642, 0, 0, 0, 0, 'Get me those ore samples!', 0);
INSERT INTO `quest_request_items` VALUES (25647, 0, 0, 0, 0, 'What have you got there?', 0);
INSERT INTO `quest_request_items` VALUES (25649, 0, 0, 0, 0, 'What have you got there?', 0);
INSERT INTO `quest_request_items` VALUES (25650, 0, 0, 0, 0, 'Well? What\'s it say?', 0);
INSERT INTO `quest_request_items` VALUES (25651, 0, 0, 0, 0, 'You want nuthin\' to do with me, $r.$b$bI\'m a touch... screwy.', 0);
INSERT INTO `quest_request_items` VALUES (25654, 0, 0, 0, 0, 'We must not tolerate the infestation of our ancestor\'s ruins by the harpies.', 0);
INSERT INTO `quest_request_items` VALUES (25656, 0, 0, 0, 0, '<Thisalee is cleaning her nails with a dagger. She looks up at you expectantly.>', 0);
INSERT INTO `quest_request_items` VALUES (25659, 0, 0, 0, 0, 'Your foe is but a short swim down this hall. Go, and bring death with you.', 0);
INSERT INTO `quest_request_items` VALUES (25661, 0, 0, 0, 0, 'A fresh heart, lungs, an\' liver be what we needin\'. The ones from his cap\'ns will have ta do though they all be likely picked.', 0);
INSERT INTO `quest_request_items` VALUES (25662, 0, 0, 0, 0, 'My captor holds the key to this cage.', 0);
INSERT INTO `quest_request_items` VALUES (25663, 0, 0, 0, 0, '<The spirit fades in and out of your vision, as though here and elsewhere at the same time.>', 0);
INSERT INTO `quest_request_items` VALUES (25668, 1, 0, 0, 0, 'Did you find any of the missing supplies?', 24742);
INSERT INTO `quest_request_items` VALUES (25672, 0, 0, 0, 0, 'Ya gots to get me lotsa those carcasses, mon! We not be rid of his foolish spirit if ya don\'t!', 0);
INSERT INTO `quest_request_items` VALUES (25673, 0, 0, 0, 0, 'Mr. P is so sad. We need to make him happy!', 0);
INSERT INTO `quest_request_items` VALUES (25682, 0, 0, 0, 0, 'You\'ve returned, $n?', 0);
INSERT INTO `quest_request_items` VALUES (25685, 0, 0, 0, 0, 'Ogre blood only takes you so far.  Do you have it?', 0);
INSERT INTO `quest_request_items` VALUES (25687, 0, 0, 0, 0, 'Trust me... whatever happened to Loramus, we can fix.  The limits of blood magic are still undefined.', 0);
INSERT INTO `quest_request_items` VALUES (25689, 0, 0, 0, 0, 'Do I know you, good $r?', 0);
INSERT INTO `quest_request_items` VALUES (25690, 0, 0, 0, 0, 'I\'m famished!  If you don\'t hurry, I might actually resort to drinking our reagents.', 0);
INSERT INTO `quest_request_items` VALUES (25691, 0, 0, 0, 0, 'Keep it down, would ya?', 0);
INSERT INTO `quest_request_items` VALUES (25692, 0, 0, 0, 0, 'Ugh, what\'s that smell?  Is that you, or the blood?', 0);
INSERT INTO `quest_request_items` VALUES (25693, 0, 0, 0, 0, '$C... you return...', 0);
INSERT INTO `quest_request_items` VALUES (25697, 0, 0, 0, 0, 'Allistarj left a piece of himself in that amulet.  Even now, I can sense his evil.', 0);
INSERT INTO `quest_request_items` VALUES (25698, 0, 0, 0, 0, 'Sevine\'s amulet... along with the others, we can reach the Rise of the Defiler.', 0);
INSERT INTO `quest_request_items` VALUES (25699, 0, 0, 0, 0, 'The third amulet, from Razelikh\'s third lieutenant.', 0);
INSERT INTO `quest_request_items` VALUES (25705, 0, 0, 0, 0, 'Neptulon guide you, landwalker.', 0);
INSERT INTO `quest_request_items` VALUES (25716, 0, 0, 0, 0, 'The cultists will not part easily with their foci.  Slay them quickly before they suspect our intent.', 0);
INSERT INTO `quest_request_items` VALUES (25722, 0, 0, 0, 0, 'Let\'s see the sediment, then.', 0);
INSERT INTO `quest_request_items` VALUES (25723, 0, 0, 0, 0, 'Are they meaty ones? Let\'s have a look.', 0);
INSERT INTO `quest_request_items` VALUES (25725, 0, 0, 0, 0, 'How much\'ve you got?', 0);
INSERT INTO `quest_request_items` VALUES (25726, 0, 0, 0, 0, 'Did you find the dumpy?', 0);
INSERT INTO `quest_request_items` VALUES (25727, 0, 0, 0, 0, 'Let\'s see it, then.', 0);
INSERT INTO `quest_request_items` VALUES (25730, 0, 0, 0, 0, 'You kill the beast?', 0);
INSERT INTO `quest_request_items` VALUES (25732, 0, 0, 0, 0, 'So, $r...$b$bYou gunna remove this curse, or what?', 0);
INSERT INTO `quest_request_items` VALUES (25734, 0, 0, 0, 0, 'Let\'s see what we\'ve got, aye?', 0);
INSERT INTO `quest_request_items` VALUES (25735, 0, 0, 0, 0, 'Well, you look like you\'re in one piece.', 0);
INSERT INTO `quest_request_items` VALUES (25736, 0, 0, 0, 0, 'Eh? What\'s that you\'ve got there?', 0);
INSERT INTO `quest_request_items` VALUES (25741, 0, 0, 0, 0, 'Have you recovered the venom sacs I requested?', 0);
INSERT INTO `quest_request_items` VALUES (25757, 0, 0, 0, 0, 'Do you have the Alliance\'s proposal? What are they offering for the drilling rights?', 0);
INSERT INTO `quest_request_items` VALUES (25758, 0, 0, 0, 0, 'Did you get a hold of some plates? Did you have to kill anyone to get them?$B$BI hope so.', 0);
INSERT INTO `quest_request_items` VALUES (25761, 0, 0, 0, 0, 'Oh good.$B$BYou\'re still alive.', 0);
INSERT INTO `quest_request_items` VALUES (25767, 0, 0, 0, 0, 'Have you recovered the ancient relics?', 0);
INSERT INTO `quest_request_items` VALUES (25771, 0, 0, 0, 0, 'Well? Draenethyst?', 0);
INSERT INTO `quest_request_items` VALUES (25772, 0, 0, 0, 0, 'Have you found a flawless draenethyst sphere?', 0);
INSERT INTO `quest_request_items` VALUES (25776, 0, 0, 0, 0, 'If a minor dragon like Sethria can cause us so many problems, I hate to see what else Twilight\'s Hammer has in store...', 0);
INSERT INTO `quest_request_items` VALUES (25777, 0, 0, 0, 0, 'Welcome to Menethil, stranger. I hope you\'ve got good news or a stiff drink with you.', 0);
INSERT INTO `quest_request_items` VALUES (25780, 0, 0, 0, 0, 'Is he dead? Is Horghast dead?', 0);
INSERT INTO `quest_request_items` VALUES (25800, 0, 0, 0, 0, 'Don\'t bother with the smaller claws, they\'re more work to shell than the meat\'s worth.', 0);
INSERT INTO `quest_request_items` VALUES (25801, 0, 0, 0, 0, 'Our merchants will not be safe until we are rid of Gobbler and those murlocs.', 0);
INSERT INTO `quest_request_items` VALUES (25805, 0, 0, 0, 0, 'Do you have the statuette?', 0);
INSERT INTO `quest_request_items` VALUES (25815, 0, 0, 0, 0, 'Oh, I\'m so very thirsty!  Won\'t you buy a drink for this poor, pathetic fool?', 0);
INSERT INTO `quest_request_items` VALUES (25818, 0, 0, 0, 0, 'This chest is securely locked.', 0);
INSERT INTO `quest_request_items` VALUES (25819, 0, 0, 0, 0, 'How can I help you, $glad:lass;?', 0);
INSERT INTO `quest_request_items` VALUES (25822, 0, 0, 0, 0, 'Have you gathered the armaments of war?', 0);
INSERT INTO `quest_request_items` VALUES (25837, 0, 0, 0, 0, 'This ramlette ain\'t gonna make itself!', 0);
INSERT INTO `quest_request_items` VALUES (25844, 0, 0, 0, 0, '<Ton Window sniffs at the air and raises his left hand.>$B$BYou have brought something for us?', 0);
INSERT INTO `quest_request_items` VALUES (25845, 0, 0, 0, 0, 'Well???', 0);
INSERT INTO `quest_request_items` VALUES (25846, 0, 0, 0, 0, 'Do you have the weapons?', 0);
INSERT INTO `quest_request_items` VALUES (25847, 0, 0, 0, 0, 'Have you armed the Grimtotem?', 0);
INSERT INTO `quest_request_items` VALUES (25848, 0, 0, 0, 0, 'Are the generals dead?', 0);
INSERT INTO `quest_request_items` VALUES (25849, 0, 0, 0, 0, 'Don\'t be coy about it, now! Bring back a good armful of \'em!', 0);
INSERT INTO `quest_request_items` VALUES (25853, 0, 0, 0, 0, 'Are they all here? We left a lot back there.', 0);
INSERT INTO `quest_request_items` VALUES (25854, 0, 0, 0, 0, 'Do you have enough eggs? I can\'t take my chances with only one. Statistics, you know.', 0);
INSERT INTO `quest_request_items` VALUES (25855, 0, 0, 0, 0, 'Did you find them?', 0);
INSERT INTO `quest_request_items` VALUES (25856, 0, 0, 0, 0, 'Got any croc hides for me?', 0);
INSERT INTO `quest_request_items` VALUES (25857, 0, 0, 0, 0, 'Did you get it? Do you have Horrorjaw\'s hide?', 0);
INSERT INTO `quest_request_items` VALUES (25865, 0, 0, 0, 0, 'There should be about eight ingots stolen.', 0);
INSERT INTO `quest_request_items` VALUES (25876, 0, 0, 0, 0, 'Did you recover the log book?', 0);
INSERT INTO `quest_request_items` VALUES (25912, 0, 0, 0, 0, 'Have you found my sketch book?', 0);
INSERT INTO `quest_request_items` VALUES (25913, 0, 0, 0, 0, 'Recover the sentinel\'s glaives, $N. Each glaive is unique to a sentinel.', 0);
INSERT INTO `quest_request_items` VALUES (25934, 0, 0, 0, 0, 'Have you recovered the blazing heart of fire?', 0);
INSERT INTO `quest_request_items` VALUES (25935, 0, 0, 0, 0, 'Hurry, $N, the pups are hungry!', 0);
INSERT INTO `quest_request_items` VALUES (25937, 1, 0, 0, 0, 'Were you able to find any artifacts down there?', 24742);
INSERT INTO `quest_request_items` VALUES (25943, 0, 0, 0, 0, 'You got food for the troops, $r?', 0);
INSERT INTO `quest_request_items` VALUES (25944, 0, 0, 0, 0, 'Were you able to gather up our gear, $r?', 0);
INSERT INTO `quest_request_items` VALUES (25947, 0, 0, 0, 0, 'Were you able to locate more of the Alliance gear, $n?', 0);
INSERT INTO `quest_request_items` VALUES (25956, 0, 0, 0, 0, 'Yes, $c?', 0);
INSERT INTO `quest_request_items` VALUES (25962, 0, 0, 0, 0, 'Tick, tock, tick, tock. Always in such a hurry. No time for innovation. What I wouldn\'t give for a lab again!', 0);
INSERT INTO `quest_request_items` VALUES (25964, 0, 0, 0, 0, 'These guys aren\'t in good shape. We may need to retreat sooner than we\'d like.', 0);
INSERT INTO `quest_request_items` VALUES (25970, 0, 0, 0, 0, 'A whole big box of amazing! That\'s what it is. I need more amazing in my life.$B$BDon\'t worry, I\'ll share.', 0);
INSERT INTO `quest_request_items` VALUES (25971, 0, 0, 0, 0, 'The rope ties the whole plan together!', 0);
INSERT INTO `quest_request_items` VALUES (25976, 0, 0, 0, 0, 'How much plunder did you get, $N?', 0);
INSERT INTO `quest_request_items` VALUES (25982, 0, 0, 0, 0, 'What\'s that smell?', 0);
INSERT INTO `quest_request_items` VALUES (25989, 0, 0, 0, 0, 'I hope this works.  My skills as a shaman are limited, and I fear that this task may be too much for us.', 0);
INSERT INTO `quest_request_items` VALUES (25992, 0, 0, 0, 0, 'In my youth, I was given some training on spearfighting.$b$b\"Stick them with the pointy end,\" I think it was.', 0);
INSERT INTO `quest_request_items` VALUES (25994, 0, 0, 0, 0, 'The naga must have some ulterior motive with these pearls.  If I had time to study them, I\'m certain I could figure it out.', 0);
INSERT INTO `quest_request_items` VALUES (25997, 1, 0, 0, 0, 'What are the Dark Irons up to?', 24742);
INSERT INTO `quest_request_items` VALUES (26022, 0, 0, 0, 0, 'I won\'t be swayed by empty claims. Have you proof of your loyalty?', 0);
INSERT INTO `quest_request_items` VALUES (26024, 0, 0, 0, 0, 'Greetings, $c.  You have a report to make?', 0);
INSERT INTO `quest_request_items` VALUES (26035, 0, 0, 0, 0, 'Have you had any luck so far?', 0);
INSERT INTO `quest_request_items` VALUES (26036, 0, 0, 0, 0, 'Our goal is the wand, $N. Focus on that for now.', 0);
INSERT INTO `quest_request_items` VALUES (26037, 0, 0, 0, 0, '$N, hello again. Have you found an azure agate yet?', 0);
INSERT INTO `quest_request_items` VALUES (26038, 0, 0, 0, 0, 'You do this, an\' I\'m sure Malin\'ll be more than pleased with you.', 0);
INSERT INTO `quest_request_items` VALUES (26039, 0, 0, 0, 0, 'This cluster of shards looks much like the Shards of Myzrael.  But the same feeling of security does not pulse through them.', 0);
INSERT INTO `quest_request_items` VALUES (26041, 0, 0, 0, 0, 'This is the Stone of Inner Binding.  It is the strongest of the binding stones and must be unlocked for the others to open.', 0);
INSERT INTO `quest_request_items` VALUES (26042, 0, 0, 0, 0, 'The Keystone is charged with tremendous geomantic energy.', 0);
INSERT INTO `quest_request_items` VALUES (26049, 0, 0, 0, 0, 'The Shards of Myzrael are now strangely quiet.', 0);
INSERT INTO `quest_request_items` VALUES (26051, 0, 0, 0, 0, 'How did the goggles work? Did everything function properly? Adjustments can be made if necessary.$b$bOh, and I\'ll need those goggles back when you\'re done. I am sure Captain O\'Breen will have many uses for them later on.', 0);
INSERT INTO `quest_request_items` VALUES (26052, 0, 0, 0, 0, 'Ahoy, $c.  What brings you to the bridge?', 0);
INSERT INTO `quest_request_items` VALUES (26079, 0, 0, 0, 0, 'Hello.  Do you have a report to submit?', 0);
INSERT INTO `quest_request_items` VALUES (26087, 0, 0, 0, 0, 'We need lots and lots of glow-juice.', 0);
INSERT INTO `quest_request_items` VALUES (26088, 0, 0, 0, 0, 'How many did you catch?', 0);
INSERT INTO `quest_request_items` VALUES (26090, 0, 0, 0, 0, '<Blam!>$b$bYou brought me something?', 0);
INSERT INTO `quest_request_items` VALUES (26092, 0, 0, 0, 0, 'Where was he?  What happened?', 0);
INSERT INTO `quest_request_items` VALUES (26095, 0, 0, 0, 0, 'Have you retrieved any Stromgarde Badges, $N?  The Syndicate must learn they cannot profit from our dead.', 0);
INSERT INTO `quest_request_items` VALUES (26096, 0, 0, 0, 0, 'I heard that you had an unpleasant encounter with putting one of the demons on your head. I can assure you that they are now all quite dead.', 0);
INSERT INTO `quest_request_items` VALUES (26107, 0, 0, 0, 0, 'The fierceness in the eyes of the raptors will be matched by those of our warriors.', 0);
INSERT INTO `quest_request_items` VALUES (26110, 0, 0, 0, 0, 'We appreciate your help in looking for that courier, $N.$B$BWhile you were gone, Kin saw an apothecary come out of that same house to the south of here. I\'m confident that they\'re up to no good--I just need proof of it before we go off and do something rash.$B$BHave you had any luck yet?', 0);
INSERT INTO `quest_request_items` VALUES (26111, 0, 0, 0, 0, 'Do you have it, $N?$B$BWith the Unfathomable\'s brain, we still may yet be able to communicate with L\'ghorek, the dying ancient one in front of us.', 0);
INSERT INTO `quest_request_items` VALUES (26122, 0, 0, 0, 0, 'Please... don\'t tell the other goblins that I\'m an environmentalist!  I don\'t think I can take the mocking!', 0);
INSERT INTO `quest_request_items` VALUES (26140, 0, 0, 0, 0, 'I SENSE... SOMETHING. COME CLOSER....', 0);
INSERT INTO `quest_request_items` VALUES (26141, 0, 0, 0, 0, 'DO... YOU HAVE... ALL THAT IS... REQUIRED?', 0);
INSERT INTO `quest_request_items` VALUES (26157, 0, 0, 0, 0, 'I don\'t know you.', 0);
INSERT INTO `quest_request_items` VALUES (26159, 0, 0, 0, 0, 'Do you have it?', 0);
INSERT INTO `quest_request_items` VALUES (26160, 0, 0, 0, 0, 'The ritual is not yet complete.', 0);
INSERT INTO `quest_request_items` VALUES (26161, 0, 0, 0, 0, 'Trust me... whatever happened to Loramus, we can fix.  The limits of blood magic are still undefined.', 0);
INSERT INTO `quest_request_items` VALUES (26163, 0, 0, 0, 0, 'Do I know you, good $r?', 0);
INSERT INTO `quest_request_items` VALUES (26164, 0, 0, 0, 0, 'Please, lower your voice.', 0);
INSERT INTO `quest_request_items` VALUES (26165, 0, 0, 0, 0, 'Ugh, what\'s that smell?  Is that you, or the blood?', 0);
INSERT INTO `quest_request_items` VALUES (26166, 0, 0, 0, 0, 'You\'ve returned, $c.', 0);
INSERT INTO `quest_request_items` VALUES (26167, 0, 0, 0, 0, 'Allistarj left a piece of himself in that amulet.  Even now, I can sense his evil.', 0);
INSERT INTO `quest_request_items` VALUES (26168, 0, 0, 0, 0, 'Sevine\'s amulet... along with the others, we can summon the Defiler.', 0);
INSERT INTO `quest_request_items` VALUES (26169, 0, 0, 0, 0, 'The third amulet, from Razelikh\'s third lieutenant.', 0);
INSERT INTO `quest_request_items` VALUES (26172, 0, 0, 0, 0, 'I\'m famished!  If you don\'t hurry, I might actually resort to drinking our reagents.', 0);
INSERT INTO `quest_request_items` VALUES (26180, 0, 0, 0, 0, 'I seethe with anger at the thought of the atrocities the Venture Co. is willing to perpetrate against us in the name of profit.', 0);
INSERT INTO `quest_request_items` VALUES (26186, 0, 0, 0, 0, 'Shayana\'s plan is a good one.  Do you have the vessel, $N?', 0);
INSERT INTO `quest_request_items` VALUES (26188, 0, 0, 0, 0, 'My fever grows worse and I find my mind drifting into delirious nightmares... I should have known that I wouldn\'t be able to handle a beast my grandfather was unable to defeat...\n\nDo you have the ingredients for the remedy? I fear if I do not clean the infection soon, it will be the death of me.', 0);
INSERT INTO `quest_request_items` VALUES (26196, 0, 0, 0, 0, 'Aye, what\'ve ye got for me?', 0);
INSERT INTO `quest_request_items` VALUES (26210, 0, 0, 0, 0, 'Did you get it yet?', 0);
INSERT INTO `quest_request_items` VALUES (26212, 0, 0, 0, 0, '<Lard rubs his enormous belly.>\n\nLard so hungry.', 0);
INSERT INTO `quest_request_items` VALUES (26213, 0, 0, 0, 0, 'Find anything on the gnolls?', 0);
INSERT INTO `quest_request_items` VALUES (26214, 0, 0, 0, 0, 'How goes the murloc hunt?', 0);
INSERT INTO `quest_request_items` VALUES (26220, 0, 0, 0, 0, 'Have you finished slaughtering the swine?', 0);
INSERT INTO `quest_request_items` VALUES (26225, 0, 0, 0, 0, 'Do you have 12 bottles? I know how to count, you know!', 0);
INSERT INTO `quest_request_items` VALUES (26226, 0, 0, 0, 0, 'Were you able to dig up some crawfish?', 0);
INSERT INTO `quest_request_items` VALUES (26227, 0, 0, 0, 0, 'Were you able to find enough fruit?', 0);
INSERT INTO `quest_request_items` VALUES (26233, 0, 0, 0, 0, 'Were you able to acquire the rice?', 0);
INSERT INTO `quest_request_items` VALUES (26235, 0, 0, 0, 0, 'Were you able to recover the rations?', 0);
INSERT INTO `quest_request_items` VALUES (26241, 0, 0, 0, 0, 'Food will be ready as soon as I have all of the ingredients! Hurry!', 0);
INSERT INTO `quest_request_items` VALUES (26244, 0, 0, 0, 0, 'You\'ve returned, $N.', 0);
INSERT INTO `quest_request_items` VALUES (26252, 0, 0, 0, 0, 'What have you got there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (26256, 0, 0, 0, 0, 'We must make haste if we\'re to rebuild the World Pillar, $N.', 0);
INSERT INTO `quest_request_items` VALUES (26259, 0, 0, 0, 0, 'You return, $N.  Did you obtain the blood?', 0);
INSERT INTO `quest_request_items` VALUES (26266, 0, 0, 0, 0, 'Good day $g sir:miss;. How may I serve you?', 0);
INSERT INTO `quest_request_items` VALUES (26268, 0, 0, 0, 0, 'How many crates did you find?', 0);
INSERT INTO `quest_request_items` VALUES (26269, 0, 0, 0, 0, 'Have you found it?  I\'d hate to return an almost-finished book back to Hemet.', 0);
