INSERT INTO `quest_request_items` VALUES (26730, 0, 0, 0, 0, 'The salt?', 0);
INSERT INTO `quest_request_items` VALUES (26731, 0, 0, 0, 0, 'I eagerly await the results.', 0);
INSERT INTO `quest_request_items` VALUES (26732, 0, 0, 0, 0, 'I\'m a little bit of an alchemist in my spare time, you know.', 0);
INSERT INTO `quest_request_items` VALUES (26733, 0, 0, 0, 0, 'There\'s something not quite healthy about this elixir, $N.  Even though I haven\'t completed my analysis, I can sense it.', 0);
INSERT INTO `quest_request_items` VALUES (26734, 0, 0, 0, 0, 'Make sure you handle that stuff carefully, $N.  We don\'t know what it is.', 0);
INSERT INTO `quest_request_items` VALUES (26735, 0, 0, 0, 0, 'What were you able to find, $n?', 0);
INSERT INTO `quest_request_items` VALUES (26740, 0, 0, 0, 0, 'Hello!  You brought me something?', 0);
INSERT INTO `quest_request_items` VALUES (26742, 0, 0, 0, 0, 'Beware: they may put up a fight.', 0);
INSERT INTO `quest_request_items` VALUES (26743, 0, 0, 0, 0, 'It\'s likely that these totemic creatures are based on some of the fantastic creatures that roam this jungle.', 0);
INSERT INTO `quest_request_items` VALUES (26744, 0, 0, 0, 0, 'Did you make it down to Bal\'lal Ruins yet?  Did you find the tablet?', 0);
INSERT INTO `quest_request_items` VALUES (26746, 0, 0, 0, 0, 'How many did you find?', 0);
INSERT INTO `quest_request_items` VALUES (26747, 0, 0, 0, 0, 'Gan\'zulah was a brute and a tyrant.  I\'ve got a feeling that this troll will be different.  This troll magic is easier than I thought it would be!', 0);
INSERT INTO `quest_request_items` VALUES (26748, 0, 0, 0, 0, 'All I ask is the raptor, $r.  Give it to me, and the gnome lives.', 0);
INSERT INTO `quest_request_items` VALUES (26760, 0, 0, 0, 0, 'Excuse me, we\'re not done yet.', 0);
INSERT INTO `quest_request_items` VALUES (26763, 0, 0, 0, 0, 'Do you have those crystals?', 0);
INSERT INTO `quest_request_items` VALUES (26765, 0, 0, 0, 0, 'Hello, $N.  Did the goblin Krazek fix my pot??', 0);
INSERT INTO `quest_request_items` VALUES (26766, 0, 0, 0, 0, 'Did you obtain the meat?', 0);
INSERT INTO `quest_request_items` VALUES (26768, 0, 0, 0, 0, 'If the snares are good enough for stone drakes, they might work on Abyssion.', 0);
INSERT INTO `quest_request_items` VALUES (26772, 0, 0, 0, 0, 'Raptor named Ohgan\'aka, still your friend.', 0);
INSERT INTO `quest_request_items` VALUES (26774, 0, 0, 0, 0, 'Extra-big spells need extra-big brains, yes.', 0);
INSERT INTO `quest_request_items` VALUES (26781, 0, 0, 0, 0, 'Do you have the Eye?', 0);
INSERT INTO `quest_request_items` VALUES (26782, 0, 0, 0, 0, 'Why is it that the OGRES always find the priceless artifacts?', 0);
INSERT INTO `quest_request_items` VALUES (26784, 0, 0, 0, 0, 'I hope the fighting within the river wasn\'t too dangerous.', 0);
INSERT INTO `quest_request_items` VALUES (26787, 0, 0, 0, 0, 'Did you get them? Did you?', 0);
INSERT INTO `quest_request_items` VALUES (26788, 0, 0, 0, 0, 'Do you have the mud? Construction of the new walls has already begun!', 0);
INSERT INTO `quest_request_items` VALUES (26799, 0, 0, 0, 0, 'You will not go far in this new life if you are unable to kill even the weakest, most brainless creatures, $N.  Do not return until you have success to report.', 0);
INSERT INTO `quest_request_items` VALUES (26801, 0, 0, 0, 0, 'Those who dally in this task might as well end up wandering about aimlessly like our fallen brothers and sisters in the village.  Let\'s hope you will fare better than them, yes? ', 0);
INSERT INTO `quest_request_items` VALUES (26802, 0, 0, 0, 0, 'Do try your best to keep yourself unharmed until I get you some armor.', 0);
INSERT INTO `quest_request_items` VALUES (26806, 0, 0, 0, 0, 'Are you ready to return to Orgrimmar?', 0);
INSERT INTO `quest_request_items` VALUES (26807, 0, 0, 0, 0, 'Did you bring those reports?', 0);
INSERT INTO `quest_request_items` VALUES (26808, 0, 0, 0, 0, 'Care to explain yourself, $c?', 0);
INSERT INTO `quest_request_items` VALUES (26815, 0, 0, 0, 0, 'Excellent... you\'re not a zombie yet.  That\'s good.$b$bOh, umm... nothing.  It\'s just that... that was a potential risk.  Did I forget to warn you?', 0);
INSERT INTO `quest_request_items` VALUES (26816, 0, 0, 0, 0, 'Did he use shimmerweed?  I\'m guessing he used shimmerweed.$b$bNo wait... amberseeds!  It\'s gotta be amberseeds.', 0);
INSERT INTO `quest_request_items` VALUES (26817, 0, 0, 0, 0, '<Dask looks eagerly at your pack as you approach.>', 0);
INSERT INTO `quest_request_items` VALUES (26818, 0, 0, 0, 0, 'Oooh... I hope they\'re soft!', 0);
INSERT INTO `quest_request_items` VALUES (26819, 0, 0, 0, 0, 'Welcome back!', 0);
INSERT INTO `quest_request_items` VALUES (26820, 0, 0, 0, 0, 'Let\'s see \'em!', 0);
INSERT INTO `quest_request_items` VALUES (26821, 0, 0, 0, 0, 'Yes, $r?  I\'m quite busy.', 0);
INSERT INTO `quest_request_items` VALUES (26823, 0, 0, 0, 0, 'You got the task?  I got the flask.', 0);
INSERT INTO `quest_request_items` VALUES (26824, 0, 0, 0, 0, 'I saw smoke.  Everything alright?', 0);
INSERT INTO `quest_request_items` VALUES (26826, 0, 0, 0, 0, 'You got something that needs mixing?', 0);
INSERT INTO `quest_request_items` VALUES (26842, 0, 0, 0, 0, 'Got some ears for me, then?', 0);
INSERT INTO `quest_request_items` VALUES (26845, 0, 0, 0, 0, 'I don\'t suppose it was Moira down there? Miracles do happen.', 0);
INSERT INTO `quest_request_items` VALUES (26855, 0, 0, 0, 0, 'Hello, $N.  Do you have word of my friend Hildelve?', 0);
INSERT INTO `quest_request_items` VALUES (26858, 0, 0, 0, 0, 'If Taragaman is dead, show me his heart.', 0);
INSERT INTO `quest_request_items` VALUES (26860, 0, 0, 0, 0, 'I\'m all ready to start cooking, do you have the bear meat for me?', 0);
INSERT INTO `quest_request_items` VALUES (26862, 0, 0, 0, 0, 'Do you have the bindings?', 0);
INSERT INTO `quest_request_items` VALUES (26863, 0, 0, 0, 0, 'Do you have that Miners\' Gear, $N?', 0);
INSERT INTO `quest_request_items` VALUES (26864, 0, 0, 0, 0, 'Any news from Algaz Station?', 0);
INSERT INTO `quest_request_items` VALUES (26871, 0, 0, 0, 0, 'You\'re alive. This is good.$B$BIs the gemstone with you?', 0);
INSERT INTO `quest_request_items` VALUES (26872, 0, 0, 0, 0, 'How many of the beasts have you slain?', 0);
INSERT INTO `quest_request_items` VALUES (26873, 0, 0, 0, 0, 'I am eager to see if you can gather enough Serpentbloom.', 0);
INSERT INTO `quest_request_items` VALUES (26875, 0, 0, 0, 0, 'We cannot let the binding be interrupted. They need your help in the back rooms - please hurry.', 0);
INSERT INTO `quest_request_items` VALUES (26876, 0, 0, 0, 0, '$N!  You\'ve returned!', 0);
INSERT INTO `quest_request_items` VALUES (26882, 0, 0, 0, 0, 'It is good to see you again, $N. What news do you bring?$B$BWas your foray successful? Did you find Thaelrid?', 0);
INSERT INTO `quest_request_items` VALUES (26883, 0, 0, 0, 0, 'Blackfathom Deeps was once an ancient night elf temple. It once housed a most powerful moonwell. Who knows what evil brews there now at the hands of the Twilight\'s Hammer.$b$bHave you made any progress in ridding the temple of their presence?', 0);
INSERT INTO `quest_request_items` VALUES (26884, 0, 0, 0, 0, 'Once I have enough samples to study, my true work can begin. With the corrupted brain stems of both the satyr and naga of Blackfathom Deeps I can try to draw a correlation between the mutations.$b$bPerhaps with that gained knowledge we can prevent the blood elves from delving deeper into deformity and evil.', 0);
INSERT INTO `quest_request_items` VALUES (26885, 0, 0, 0, 0, 'Your task is not an easy one, my friend.$B$BBut if you get me the manuscript, $N...then your reward will be great and we will gain a better understanding of what it is that the Twilight\'s Hammer and the naga are up to.', 0);
INSERT INTO `quest_request_items` VALUES (26887, 0, 0, 0, 0, 'Have you got the fillings?', 0);
INSERT INTO `quest_request_items` VALUES (26891, 0, 0, 0, 0, 'Have you been successful in locating the fathom core?  Without it we\'ll have no idea what the Twilight\'s Hammer is exactly up to down there.', 0);
INSERT INTO `quest_request_items` VALUES (26899, 0, 0, 0, 0, 'Revenge...reward...', 0);
INSERT INTO `quest_request_items` VALUES (26901, 0, 0, 0, 0, 'Ah, I see you have returned. Were you able to procure any Kraul Guano?', 0);
INSERT INTO `quest_request_items` VALUES (26905, 0, 0, 0, 0, '<The Spirit of Agamaggan stares at you patiently.>', 0);
INSERT INTO `quest_request_items` VALUES (26925, 0, 0, 0, 0, 'Araj is a formidable opponent.  Be prepared.', 0);
INSERT INTO `quest_request_items` VALUES (26928, 0, 0, 0, 0, 'Now don\'t you stink all to high hell! I\'m assumin\' that\'s because of the murlocs, but it\'s hard to tell.', 0);
INSERT INTO `quest_request_items` VALUES (26929, 0, 0, 0, 0, 'So did you get those crocolisk things or did I make that up?', 0);
INSERT INTO `quest_request_items` VALUES (26934, 0, 0, 0, 0, 'Animal tissue holds onto disease so much better than plant tissue, doesn\'t it?', 0);
INSERT INTO `quest_request_items` VALUES (26961, 0, 0, 0, 0, 'Do you have those idols?  We must study them and report our findings to Ironforge!', 0);
INSERT INTO `quest_request_items` VALUES (26992, 0, 0, 0, 0, 'Have you collected the ferocious doomweed I requested?', 0);
INSERT INTO `quest_request_items` VALUES (26993, 0, 0, 0, 0, 'I don\'t want to stay in a monastery packed full of undead-hating zealots for much longer. Surely you can understand why. Don\'t speak to me if you don\'t have the book with you.', 0);
INSERT INTO `quest_request_items` VALUES (26995, 0, 0, 0, 0, 'Remember, I can only use clean beast guts for the new strain of blight we\'re developing.', 0);
INSERT INTO `quest_request_items` VALUES (26996, 0, 0, 0, 0, '<Dominic stares at you.>', 0);
INSERT INTO `quest_request_items` VALUES (26998, 0, 0, 0, 0, 'Have you exterminated the Vile Fin murlocs of the Dawning Isles?', 0);
INSERT INTO `quest_request_items` VALUES (26999, 0, 0, 0, 0, 'Once the animals are healed, our duties here will be nearly complete.', 0);
INSERT INTO `quest_request_items` VALUES (27004, 0, 0, 0, 0, 'Those plans are the key to everything.', 0);
INSERT INTO `quest_request_items` VALUES (27005, 0, 0, 0, 0, 'Those plans are the key to everything.', 0);
INSERT INTO `quest_request_items` VALUES (27011, 0, 0, 0, 0, 'Some of the older lumberjacks tell me that the Redpine lived in the area several years ago, but left when the plague arrived.  That doesn\'t make it OK for them to steal, though.', 0);
INSERT INTO `quest_request_items` VALUES (27012, 0, 0, 0, 0, 'I don\'t want to order some sort of mass gnoll genocide because of a mother\'s worries.  I\'d just like to feel safe in my own home.', 0);
INSERT INTO `quest_request_items` VALUES (27015, 0, 0, 0, 0, 'I am pleased to meet you, $N.', 0);
INSERT INTO `quest_request_items` VALUES (27017, 0, 0, 0, 0, 'I wouldn\'t expect you to indulge the whims of a nostalgic old man, $N.  Still, if you find yourself in the area...', 0);
INSERT INTO `quest_request_items` VALUES (27019, 0, 0, 0, 0, 'I\'m not one for politics; I care not if your affiliations are for either Horde or Alliance.  I am more interested in squelching direct and dire threats to all the races... and we have quite a large one in here.$B$BIf you\'re willing to be a true hero for all people, then hand me the oathstone I have given you as a sign of commitment.  I\'m... not from around these parts; an oathstone is important to people of my kind as a bond of agreement.', 0);
INSERT INTO `quest_request_items` VALUES (27025, 0, 0, 0, 0, 'Handle them safely, now!', 0);
INSERT INTO `quest_request_items` VALUES (27028, 0, 0, 0, 0, 'How go those wasps?', 0);
INSERT INTO `quest_request_items` VALUES (27029, 0, 0, 0, 0, 'Do you have the tusks?', 0);
INSERT INTO `quest_request_items` VALUES (27030, 0, 0, 0, 0, 'What\'ve you got for me?', 0);
INSERT INTO `quest_request_items` VALUES (27031, 0, 0, 0, 0, 'Only the finest feathers will do!', 0);
INSERT INTO `quest_request_items` VALUES (27036, 0, 0, 0, 0, 'Ah, $n! Back so soon? No doubt you\'ve been at the hunt again? Don\'t worry if you\'ve had some setbacks, in the long run it will make you better!$b$b...You couldn\'t get much worse, anyway...', 0);
INSERT INTO `quest_request_items` VALUES (27037, 0, 0, 0, 0, 'Great job, $n! We really showed him! Here, give me the head and I\'ll take care of it.', 0);
INSERT INTO `quest_request_items` VALUES (27053, 0, 0, 0, 0, 'An access panel to the cauldron remains securely fastened.  In order to open it, you will need to find the key.', 0);
INSERT INTO `quest_request_items` VALUES (27054, 0, 0, 0, 0, 'You take a look at the paperwork Malicia was carrying...', 0);
INSERT INTO `quest_request_items` VALUES (27056, 0, 0, 0, 0, 'Kneel before your queen, $N.', 0);
INSERT INTO `quest_request_items` VALUES (27057, 0, 0, 0, 0, 'Gahrron\'s Withering houses a viciously potent plague cauldron.  Once we have a sample of the plague it spews forth, we will be able to determine whether the Cult of the Damned is truly responsible for continuing to poison the land.', 0);
INSERT INTO `quest_request_items` VALUES (27070, 0, 0, 0, 0, 'Do you have what I need?', 0);
INSERT INTO `quest_request_items` VALUES (27077, 0, 0, 0, 0, 'Did you tear up the spikes?', 0);
INSERT INTO `quest_request_items` VALUES (27082, 0, 0, 0, 0, 'We are Forsaken! This is how we do it, $c.', 0);
INSERT INTO `quest_request_items` VALUES (27096, 0, 0, 0, 0, 'And the orcs?', 0);
INSERT INTO `quest_request_items` VALUES (27100, 0, 0, 0, 0, '<You scroll through the notes, trying to put them in order.>', 0);
INSERT INTO `quest_request_items` VALUES (27105, 0, 0, 0, 0, 'Do you carry the essence, or did the task prove too difficult?', 0);
INSERT INTO `quest_request_items` VALUES (27107, 0, 0, 0, 0, '$R has book? ', 0);
INSERT INTO `quest_request_items` VALUES (27108, 0, 0, 0, 0, '$N, you have stupid elf\'s web?', 0);
INSERT INTO `quest_request_items` VALUES (27112, 0, 0, 0, 0, 'Did you slay those abominations?', 0);
INSERT INTO `quest_request_items` VALUES (27126, 0, 0, 0, 0, 'Are those spare parts you have there?', 0);
INSERT INTO `quest_request_items` VALUES (27135, 0, 0, 0, 0, 'You have the heart yet?', 0);
INSERT INTO `quest_request_items` VALUES (27143, 0, 0, 0, 0, 'Bow your head in my presence, $r. I am a noble, after all, and you are a copperless peasant.', 0);
INSERT INTO `quest_request_items` VALUES (27176, 0, 0, 0, 0, 'Yes, $n?', 0);
INSERT INTO `quest_request_items` VALUES (27177, 0, 0, 0, 0, 'How much were you able to recover?', 0);
INSERT INTO `quest_request_items` VALUES (27179, 0, 0, 0, 0, 'What\'dya have there, $gfriend:young lady;?', 0);
INSERT INTO `quest_request_items` VALUES (27180, 0, 0, 0, 0, 'Have you recovered the insignias?', 0);
INSERT INTO `quest_request_items` VALUES (27192, 0, 0, 0, 0, 'Did you find any?\n', 0);
INSERT INTO `quest_request_items` VALUES (27193, 0, 0, 0, 0, 'What is your status?', 0);
INSERT INTO `quest_request_items` VALUES (27226, 0, 0, 0, 0, 'Don\'t let my sea dogs die!', 0);
INSERT INTO `quest_request_items` VALUES (27254, 0, 0, 0, 0, 'What\'s that you have there, $n? Evidence from the remains of the Shady Rest Inn?', 0);
INSERT INTO `quest_request_items` VALUES (27255, 0, 0, 0, 0, 'Something for me, $c?', 0);
INSERT INTO `quest_request_items` VALUES (27260, 0, 0, 0, 0, 'Have you found any leads on what happened at the Shady Rest Inn?', 0);
INSERT INTO `quest_request_items` VALUES (27302, 0, 0, 0, 0, 'I wonder what happens when you douse an elemental with that stuff. Jumbo-super-bonfire-elemental? I\'m thinkin\' so. We\'ll have to try.', 0);
INSERT INTO `quest_request_items` VALUES (27303, 0, 0, 0, 0, 'Did you recover the hearts?', 0);
INSERT INTO `quest_request_items` VALUES (27305, 0, 0, 0, 0, 'The painting, $N. Do you have the painting?', 0);
INSERT INTO `quest_request_items` VALUES (27342, 0, 0, 0, 0, 'Did you gather the wolfsbane?', 0);
INSERT INTO `quest_request_items` VALUES (27345, 0, 0, 0, 0, 'Recover the F.C.D.', 0);
INSERT INTO `quest_request_items` VALUES (27352, 0, 0, 0, 0, 'Is that the vial of Stratholme Holy Water?$B$BWait, is that just regular water? Don\'t play games with ol\' Stonebruiser!', 0);
INSERT INTO `quest_request_items` VALUES (27359, 0, 0, 0, 0, 'Were you able to obtain the essences? Be careful, they\'re deadly spellcasters.', 0);
INSERT INTO `quest_request_items` VALUES (27364, 0, 0, 0, 0, 'Status?', 0);
INSERT INTO `quest_request_items` VALUES (27368, 0, 0, 0, 0, 'Oh boy... Fiona is going to be madder than a wet harpy.  You\'d better hurry.', 0);
INSERT INTO `quest_request_items` VALUES (27369, 0, 0, 0, 0, 'Giddy\'s got you mixed up in all of this now?', 0);
INSERT INTO `quest_request_items` VALUES (27372, 0, 0, 0, 0, 'I should have guessed it.  Tarenar made you get me a gift?  That thoughtless idiot!$b$bUmm... what was the gift, anyhow?', 0);
INSERT INTO `quest_request_items` VALUES (27384, 0, 0, 0, 0, 'Did you find my doll?', 0);
INSERT INTO `quest_request_items` VALUES (27385, 0, 0, 0, 0, 'Any news of my Pammy?', 0);
INSERT INTO `quest_request_items` VALUES (27386, 0, 0, 0, 0, 'There is something timely about your visit.  You\'re not from the future, are you?', 0);
INSERT INTO `quest_request_items` VALUES (27387, 0, 0, 0, 0, 'Did you find the skull and the sword, $N?', 0);
INSERT INTO `quest_request_items` VALUES (27388, 0, 0, 0, 0, 'Do you have the relics, $N?', 0);
INSERT INTO `quest_request_items` VALUES (27389, 0, 0, 0, 0, 'Do you have the skulls?  They are critical for my spell, which will give us the chance to save Darrowshire.  Maybe.', 0);
INSERT INTO `quest_request_items` VALUES (27392, 0, 0, 0, 0, 'Daddy loved his animal sword.  He seemed sad when he didn\'t have it anymore.', 0);
INSERT INTO `quest_request_items` VALUES (27397, 0, 0, 0, 0, 'Did you bring the materials I\'ll need for your weapon?', 0);
INSERT INTO `quest_request_items` VALUES (27402, 0, 0, 0, 0, 'Did you bring the materials I\'ll need for your weapon?', 0);
INSERT INTO `quest_request_items` VALUES (27407, 0, 0, 0, 0, '<Brogg looks at you hopefully.>$B$BYou bring Brogg feathers?', 0);
INSERT INTO `quest_request_items` VALUES (27414, 0, 0, 0, 0, 'With the hearts and tongues of scorched offspring of the black dragon army, I can decipher what wretched beast introduced her spawn into our home. Perhaps with a greater knowledge of our aggressors, we can seek to destroy them once and for all and reclaim Stonemaul Village from ruin.$b$bOf course, convincing Mok\'Morokk to move his lethargic, fat mass out of this muck hole is going to be an epic quest in and of itself...', 0);
INSERT INTO `quest_request_items` VALUES (27432, 0, 0, 0, 0, 'Did you find the hideout, $N?  Did you take care of Zaeldarr?', 0);
INSERT INTO `quest_request_items` VALUES (27444, 0, 0, 0, 0, 'Rend will not fall without a fight.', 0);
INSERT INTO `quest_request_items` VALUES (27451, 0, 0, 0, 0, 'How are things looking in Corin\'s Crossing?', 0);
INSERT INTO `quest_request_items` VALUES (27452, 0, 0, 0, 0, 'Everything going alright so far, $N?', 0);
INSERT INTO `quest_request_items` VALUES (27456, 0, 0, 0, 0, 'You will grow to tolerate the taste, $r.  I did.', 0);
INSERT INTO `quest_request_items` VALUES (27458, 0, 0, 0, 0, 'You\'ve been fightin\' undead.  Did ya find Browman?', 0);
INSERT INTO `quest_request_items` VALUES (27479, 0, 0, 0, 0, '<Deacon Andaal is deep in prayer.>', 0);
INSERT INTO `quest_request_items` VALUES (27488, 0, 0, 0, 0, 'Northdale is home to a few ghosts and banshees.  Nothing ol\' Giddy can\'t handle.', 0);
INSERT INTO `quest_request_items` VALUES (27489, 0, 0, 0, 0, 'We have no time, $N!  We must press on!', 0);
INSERT INTO `quest_request_items` VALUES (27508, 0, 0, 0, 0, 'Twilight dragons were born of these disgusting experiments of theirs. The Twilight\'s Hammer have gone too far. Deathwing has gone too far.', 0);
INSERT INTO `quest_request_items` VALUES (27517, 0, 0, 0, 0, 'What have you found, $n?', 0);
INSERT INTO `quest_request_items` VALUES (27523, 0, 0, 0, 0, 'I hope you\'ve done away with that creature.', 0);
INSERT INTO `quest_request_items` VALUES (27531, 0, 0, 0, 0, 'You got the stuff?', 0);
INSERT INTO `quest_request_items` VALUES (27534, 0, 0, 0, 0, 'Do you have my receipt book?  I can\'t wait to get back to business!', 0);
INSERT INTO `quest_request_items` VALUES (27536, 0, 0, 0, 0, 'Hey, you get those things I asked you about?', 0);
INSERT INTO `quest_request_items` VALUES (27538, 0, 0, 0, 0, 'What is this I keep hearing about the muck of the Verrall River?', 0);
INSERT INTO `quest_request_items` VALUES (27539, 0, 0, 0, 0, 'You seem to have your hands full.  Are those skulls I see?', 0);
INSERT INTO `quest_request_items` VALUES (27541, 0, 0, 0, 0, 'Any news, $n?', 0);
INSERT INTO `quest_request_items` VALUES (27549, 0, 0, 0, 0, 'So?$b$bFind anything interesting?', 0);
INSERT INTO `quest_request_items` VALUES (27551, 0, 0, 0, 0, 'What?  Did you find something?', 0);
INSERT INTO `quest_request_items` VALUES (27586, 0, 0, 0, 0, 'Do you have the shells? Hand them here!$B$B...Gently!', 0);
INSERT INTO `quest_request_items` VALUES (27587, 0, 0, 0, 0, 'You got those tails? Don\'t be tryin\' to push no hacked-up ones on me!', 0);
INSERT INTO `quest_request_items` VALUES (27592, 0, 0, 0, 0, 'You got the goods, \"bro\"?', 0);
INSERT INTO `quest_request_items` VALUES (27596, 0, 0, 0, 0, 'Do you have the Heart of the Mountain?  To me, its value is without limit.', 0);
INSERT INTO `quest_request_items` VALUES (27599, 0, 0, 0, 0, 'You get those ditty bags? I hope you left the goblins there!', 0);
INSERT INTO `quest_request_items` VALUES (27600, 0, 0, 0, 0, 'You got it? Did you get it? Did you try the cannon? You didn\'t explode!', 0);
INSERT INTO `quest_request_items` VALUES (27604, 0, 0, 0, 0, 'Were you able to kill Jammal\'an? I\'ll need proof that the deed is done.', 0);
INSERT INTO `quest_request_items` VALUES (27614, 0, 0, 0, 0, 'I hope you gave them a fight.', 0);
INSERT INTO `quest_request_items` VALUES (27623, 0, 0, 0, 0, 'You wipe away the sand from the hieroglyphs only for them to become covered in a new layer.  You\'d best hurry.', 0);
INSERT INTO `quest_request_items` VALUES (27624, 0, 0, 0, 0, 'Find anything interesting, $n?', 0);
INSERT INTO `quest_request_items` VALUES (27626, 0, 0, 0, 0, 'Are they safe?', 0);
INSERT INTO `quest_request_items` VALUES (27632, 0, 0, 0, 0, 'This might be the map you\'re after.', 0);
INSERT INTO `quest_request_items` VALUES (27653, 0, 0, 0, 0, 'How many pendants do you have?', 0);
INSERT INTO `quest_request_items` VALUES (27663, 0, 0, 0, 0, 'Have you got those drumsticks?', 0);
INSERT INTO `quest_request_items` VALUES (27682, 0, 0, 0, 0, 'Do you have the cores with you, or are you just here to chat? Perhaps we should stop this whole charade and enjoy some snacks and fine wine instead?', 0);
INSERT INTO `quest_request_items` VALUES (27696, 0, 0, 0, 0, 'Do you have the axe?', 0);
INSERT INTO `quest_request_items` VALUES (27699, 0, 0, 0, 0, 'Whatcha got there, pal?', 0);
INSERT INTO `quest_request_items` VALUES (27706, 0, 0, 0, 0, 'You\'re back, $N!', 0);
INSERT INTO `quest_request_items` VALUES (27713, 0, 0, 0, 0, 'Are you going to listen or not?', 0);
INSERT INTO `quest_request_items` VALUES (27737, 0, 0, 0, 0, 'Do you have the core? Our men are dying in here!', 0);
INSERT INTO `quest_request_items` VALUES (27739, 0, 0, 0, 0, 'Have you defeated Hogger?', 0);
INSERT INTO `quest_request_items` VALUES (27742, 0, 0, 0, 0, 'How much do you think you can carry?', 0);
INSERT INTO `quest_request_items` VALUES (27750, 0, 0, 0, 0, 'Hungry.', 0);
INSERT INTO `quest_request_items` VALUES (27751, 0, 0, 0, 0, 'Show me the insignias you\'ve collected, $c.', 0);
INSERT INTO `quest_request_items` VALUES (27760, 0, 0, 0, 0, '<The terminal whirrs as its mechanisms become active.>', 0);
