INSERT INTO `quest_request_items` VALUES (28884, 0, 0, 0, 0, 'Were you victorious against the Alliance?', 0);
INSERT INTO `quest_request_items` VALUES (29100, 0, 0, 0, 0, 'Dis won\'t be no easy task, $n.', 0);
INSERT INTO `quest_request_items` VALUES (29121, 0, 0, 0, 0, 'Do ya have me boots?  Grant a dwarf his last request will ya?', 0);
INSERT INTO `quest_request_items` VALUES (29125, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_request_items` VALUES (29135, 0, 0, 0, 0, 'Well, $c?$b$bHave you acquired the necessary materials?', 0);
INSERT INTO `quest_request_items` VALUES (29138, 0, 0, 0, 0, 'To our fortune, Ragnaros\' forces far more often injure our warriors than outright kill them. Their attacks naturally cauterize the wounds they inflict. It\'s very painful, but it\'s better than death.', 0);
INSERT INTO `quest_request_items` VALUES (29139, 0, 0, 0, 0, 'Did you find some places to plant them? I realize it\'s a bit barren out there.', 0);
INSERT INTO `quest_request_items` VALUES (29147, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_request_items` VALUES (29160, 0, 0, 0, 0, 'It appears the Cinderweb Spinners are the caretakers of Beth\'tilac\'s brood.', 0);
INSERT INTO `quest_request_items` VALUES (29166, 0, 0, 0, 0, 'Let\'s see... how many have you managed to gather?', 0);
INSERT INTO `quest_request_items` VALUES (29179, 0, 0, 0, 0, 'We cannot allow these elementals to take any ground. They\'re not likely to lapse in their attacks long enough for us to take it back.', 0);
INSERT INTO `quest_request_items` VALUES (29181, 0, 0, 0, 0, 'It is good to see you again. ', 0);
INSERT INTO `quest_request_items` VALUES (29186, 0, 0, 0, 0, 'Have ya eliminated Hex Lord Malacrass?', 0);
INSERT INTO `quest_request_items` VALUES (29199, 0, 0, 0, 0, 'The sooner we can break through our invasion portal, the better.', 0);
