INSERT INTO `quest_request_items` VALUES (29246, 0, 0, 0, 0, 'These hearts will give us exactly what Hamuul needs to rest.', 0);
INSERT INTO `quest_request_items` VALUES (29247, 0, 0, 0, 0, 'There are some who say that the sulfur in those wrappings has curative properties. Personally, I am unconvinced, but I am willing to endure the smell on the off-chance that it does.', 0);
INSERT INTO `quest_request_items` VALUES (29248, 0, 0, 0, 0, 'Do you have the scales? I hesitate to cut the Arch Druid again, but we must relieve the swelling somehow.', 0);
INSERT INTO `quest_request_items` VALUES (29252, 0, 0, 0, 0, 'Did you find something, $n?', 0);
INSERT INTO `quest_request_items` VALUES (29255, 0, 0, 0, 0, 'The Lasher is growing quite quickly.', 0);
INSERT INTO `quest_request_items` VALUES (29257, 0, 0, 0, 0, 'Fascinating! The more time I spend in the Firelands, the more I see there is an ecosystem at work here.', 0);
INSERT INTO `quest_request_items` VALUES (29265, 0, 0, 0, 0, 'They could be growing anywhere. Keep your eyes open.', 0);
INSERT INTO `quest_request_items` VALUES (29274, 0, 0, 0, 0, 'Any information we can get will be instrumental in helping our men defeat Shannox.', 0);
INSERT INTO `quest_request_items` VALUES (29275, 0, 0, 0, 0, 'Trust me - whatever information we can gather here will go far to helping us understand the source of Fandral\'s powers.', 0);
INSERT INTO `quest_request_items` VALUES (29276, 0, 0, 0, 0, 'Be careful with those fluids, $N. We don\'t want them spilling anywhere.', 0);
INSERT INTO `quest_request_items` VALUES (29278, 0, 0, 0, 0, 'How\'s it going? Found any meteorites yet?', 0);
