INSERT INTO `quest_request_items` VALUES (29287, 0, 0, 0, 0, 'Were you able to recover an egg?', 0);
INSERT INTO `quest_request_items` VALUES (29288, 0, 0, 0, 0, 'I can\'t make up my mind on how to regard these creatures. They act much like other avians, but they are very obviously native to this fiery place.$B$BI wonder why Ragnaros and his minions have not made attempts to rally them against us...', 0);
