INSERT INTO `quest_request_items` VALUES (29528, 0, 0, 0, 0, 'Were you successful? We have a lot riding on this operation, $N.', 0);
INSERT INTO `quest_request_items` VALUES (29529, 0, 0, 0, 0, 'Did you find any demons in there?', 0);
INSERT INTO `quest_request_items` VALUES (29530, 0, 0, 0, 0, 'Were you able to dispose of the foul creature?', 0);
INSERT INTO `quest_request_items` VALUES (29535, 0, 0, 0, 0, 'You\'re back! Even better, you\'re not dead! Well done. Better than anyone else so far.', 0);
INSERT INTO `quest_request_items` VALUES (29538, 0, 0, 0, 0, 'You have that blood, darlin\'?  Might be kind of important if we asked you to get it, you know?', 0);
