INSERT INTO `quest_request_items` VALUES (29567, 0, 0, 0, 0, 'You have returned.  Does this mean that The Black Stalker is no more?', 0);
INSERT INTO `quest_request_items` VALUES (29574, 0, 0, 0, 0, 'If you don\'t have what I\'m looking for, stop wasting my time.', 0);
INSERT INTO `quest_request_items` VALUES (29575, 0, 0, 0, 0, 'Were you interested in the bounty on Shaffar\'s head?', 0);
